    private void formWindowOpened(java.awt.event.WindowEvent evt) {//GEN-FIRST:event_formWindowOpened
        // amount of hours panel:
        CoreCalc calculator = new CoreCalc();
        int hrsDS = calculator.getDS();
        jDShrsField.setText(String.valueOf(hrsDS));
        
        int hrsND = calculator.getND();
        jNDhrsField.setText(String.valueOf(hrsND));
        
        int hrsSI = calculator.getSI();
        jSIhrsField.setText(String.valueOf(hrsSI));
        
        int holsHrs = calculator.getHols();
        jHoldHrsField.setText(String.valueOf(holsHrs));
        
        double contractHrs = calculator.getContractHrs();
        jContractHrsField.setText(String.valueOf(contractHrs));
        
        //pay rate panel:
        double hourlyRate = calculator.getHourlyRate();
        jHourlyRateField.setText(String.valueOf(hourlyRate));
        
        double sleepInRate = calculator.getSleepInRate();
        jSleepInRateField.setText(String.valueOf(sleepInRate));
        
        double topUpRate = calculator.getTopUpRate();
        jTopUpRateField.setText(String.valueOf(topUpRate));
        
        double onCallRate = calculator.getOnCallRate();
        jOnCallRateField.setText(String.valueOf(onCallRate));
        
    }//GEN-LAST:event_formWindowOpened

    private void jSaveButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jSaveButtonActionPerformed
        CoreCalc calculator = new CoreCalc();
        int newHrsDS = Integer.parseInt(jDShrsField.getText());
        calculator.setDS(newHrsDS);
        
        int newHrsND = Integer.parseInt(jNDhrsField.getText());
        calculator.setND(newHrsND);
        
        int newHrsSI = Integer.parseInt(jSIhrsField.getText());
        calculator.setSI(newHrsSI);
        
        int newHrsHols = Integer.parseInt(jHoldHrsField.getText());
        calculator.setHols(newHrsHols);
        
        double newContractHrs = Double.parseDouble(jContractHrsField.getText());
        calculator.setContractHrs(newContractHrs);
        
        double newHourlyRate = Double.parseDouble(jHourlyRateField.getText());
        calculator.setHourlyRate(newHourlyRate);
        
        double newSleepInRate = Double.parseDouble(jSleepInRateField.getText());
        calculator.setSleepInRate(newSleepInRate);
        
        double newTopUpRate = Double.parseDouble(jTopUpRateField.getText());
        calculator.setTopUpRate(newTopUpRate);
        
        double newOnCallRate = Double.parseDouble(jOnCallRateField.getText());
        calculator.setOnCallRate(newOnCallRate);
        
        setVisible(false);
    }//GEN-LAST:event_jSaveButtonActionPerformed

